document.addEventListener("DOMContentLoaded",function(){const faqQuestions=document.querySelectorAll(".faq-question");faqQuestions.forEach(question=>{question.addEventListener("click",function(){const answer=this.nextElementSibling;const isOpen=answer.classList.contains("open");document.querySelectorAll(".faq-answer").forEach(ans=>{ans.classList.remove("open");ans.style.maxHeight=null});document.querySelectorAll(".faq-question").forEach(q=>q.classList.remove("active"));if(!isOpen){answer.classList.add("open");answer.style.maxHeight=answer.scrollHeight+"px";this.classList.add("active")}})})});